<template>  <router-view/></template><style lang="less">  html,body{    background: #f5f5f5;  }</style><script>  import {mapMutations,mapState} from 'vuex'  export default {    name: 'app',    computed:{      ...mapState([        'demoUrl',        'docsUrl'      ])    },    methods:{      ...mapMutations([        'SET_DEMO_URL'      ]),    },    watch:{      $route(to,from){        if(to.meta.isDemo){          this.SET_DEMO_URL(`#/demo/${to.name}`)        }else{          this.SET_DEMO_URL(`#/demo`)        }      },      docsUrl(newvalue){        if(newvalue !== '/docs'){          window.parent.location.href=`#${newvalue}`        }      }    }  }</script>