<template>  <div class="navbar" :style="{height:navbarHeight}">    <div class="navitem" v-for="item in nav">      <h2>{{item.name}}</h2>      <div class="group" v-for="group in item.groups">        <div v-if="group.groupName" class="groupName">{{group.groupName}}</div>        <router-link v-for="list in group.list" :to="`/docs/${list.name}`">  {{list.title}} </router-link>      </div>    </div>    <div>&nbsp;</div>    <div>&nbsp;</div>  </div></template><script>  import {RouterLink} from 'vue-router'  import NavConfig from '../../nav.config'  export default {    name: "xNavlist",    data(){      return {        navbarHeight:"150px",        nav:NavConfig.nav      }    },    methods:{      setNavHeight(){        let height = window.innerHeight        this.navbarHeight = `${height}px`      }    },    created(){      let me = this      this.setNavHeight()      window.addEventListener('resize',()=>{        me.setNavHeight()      })    }  }</script><style scoped lang="less">  .navbar{    position: sticky;    top: 0px;    width: 280px;    border-right: 1px solid #efefef;    bottom: 0;    height: 100%;    overflow-x: auto;    color: #455a63;    float: left;    background: #ffffff;  }  .navitem{    padding:0 30px;    h2{      font-size: 18px;      color: #555;    }    a{      display: block;      line-height: 35px;      color: #777;      font-size: 14px;      &:hover{        color: #3283fa;      }      &.router-link-active{        color: #3283fa;      }    }    .groupName{      font-size: 12px;      line-height: 35px;      color: #999;    }  }</style>