<template>  <div class="demo-index">    <h1 class="xui-title">      <div class="imgbox">        <img src="https://healen.github.io/xui/img/logo.43faec05.png"/>      </div>      <span>Xui</span>    </h1>    <p class="discribe">一款专注于，前端的Vue解决方案组件库。</p>    <x-collapse v-model="active">      <x-collapse-item class="navitem" v-for="group in groups" :title="group.groupName" :name="group.groupName">        <x-cell                class="xcontent"                v-if="!navItem.disabled"                v-for="(navItem, index) in group.list"                :key="index"                :to="'/demo/'+ navItem.name"                :title="navItem.title"                @click="changeUrl('/docs/'+navItem.name)"                is-link        />      </x-collapse-item>    </x-collapse>  </div></template><script>  import NavConf from '../nav.config'  import {mapMutations} from 'vuex'  export default {    name: "demoIndex",    data(){      return {        groups:NavConf.nav[1].groups,        active: []      }    },    methods:{      ...mapMutations([        'SET_DOCS_URL'      ]),      changeUrl(url){        let {hash} = window.parent.location        if(hash.indexOf('#/demo')<0){            this.SET_DOCS_URL(url)            window.parent.location.href=`#${url}`        }      }    }  }</script><style  lang="less">  .demo-index{    .x-collapse-item__content{      padding: 2px;    }    .discribe{      text-align: center;      color: #555;      margin-bottom: 20px;      font-size: 14px;    }    .navitem{      margin: 12px;    }    .xui-title{      margin: 20px auto;      display: flex;      align-items: center;      justify-content: center;      color: #555;      .imgbox{        width: 30px;        height: 30px;        background: #ffffff;        padding: 8px;        border-radius: 50%;        margin-right: 10px;      }      img{        width: 100%;      }      span{        font-size: 40px;      }    }  }</style>