<template>  <demo-section>    <demo-block title="基础用法">      <x-button type="warning" @click="show=!show">        显示      </x-button>      <x-actionsheet v-model="show" :actions="actions" @select="onSelect"/>    </demo-block>    <demo-block title="带取消按钮的 Actionsheet">      <x-button type="warning" @click="show1=!show1">        显示      </x-button>      <x-actionsheet              v-model="show1"              :actions="actions1"              cancel-text="取消"              @select="onSelect"      />    </demo-block>    <demo-block title="带标题的 Actionsheet">      <x-button type="warning" @click="show2=!show2">        显示      </x-button>      <x-actionsheet v-model="show2" title="标题">        <p>一些内容</p>      </x-actionsheet>    </demo-block>  </demo-section></template><script>  import demoSection from '../../common/demoSection'  import demoBlock from '../../common/demoBlock'  export default {    name: "index",    components: {      demoSection,      demoBlock    },    data() {      return {        show: false,        show1: false,        show2:false,        actions: [          {            name: '选项'          },          {            name: '选项',            subname: '描述信息'          },          {            loading: true          },          {            name: '禁用选项',            disabled: true          }        ],        actions1: [          {            name: '支付宝'          },          {            name: '微信',          }        ]      };    },    methods: {      onSelect(item) {        this.show = false;        this.$toast(item.name);      },      onSelect1(item) {        this.show1 = false;        this.$toast(item.name);      }    }  }</script><style scoped lang="less"></style>