<template>  <demo-section>    <demo-block title="基础用法">      <x-checkbox v-model="checked">复选框</x-checkbox>    </demo-block>    <demo-block title="禁用状态">      <x-checkbox v-model="checked1" disabled>复选框</x-checkbox>    </demo-block>    <demo-block title="自定义图标">      <x-checkbox v-model="checked2">        自定义图标        <img class="icon" slot="icon" slot-scope="props" :src="props.checked ? icon.active : icon.normal">      </x-checkbox>    </demo-block>    <demo-block title="Checkbox Group">      <x-checkbox-group v-model="result">        <x-checkbox                v-for="(item, index) in list"                :key="item"                :name="item"        >          复选框 {{ item }}        </x-checkbox>      </x-checkbox-group>    </demo-block>    <demo-block title="设置最大可选数">      <x-checkbox-group v-model="result1" :max="2">        <x-checkbox                v-for="(item, index) in list1"                :key="item"                :name="item"        >          复选框 {{ item }}        </x-checkbox>      </x-checkbox-group>    </demo-block>    <demo-block title="与 Cell 组件一起使用">      <x-checkbox-group v-model="result2">        <x-cell-group>          <x-cell                  v-for="(item,index) in list2"                  clickable                  :key="item"                  :title="`复选框 ${item}`"                  @click="toggle(index)"          >            <x-checkbox :name="item" ref="checkboxes" />          </x-cell>        </x-cell-group>      </x-checkbox-group>    </demo-block>  </demo-section></template><script>  import demoSection from '../../common/demoSection'  import demoBlock from '../../common/demoBlock'  export default {    name: "index",    components: {      demoSection,      demoBlock    },    data() {      return {        checked: true,        checked1: true,        checked2: true,        icon: {          normal: require('./img/icon-normal.png'),          active: require('./img/icon-active.png')        },        list: ['a', 'b', 'c'],        result: ['a', 'b'],        list1: ['a', 'b', 'c'],        result1: ['a'],        list2: ['a', 'b', 'c'],        result2: ['a']      };    },    methods:{      toggle(index) {        this.$refs.checkboxes[index].toggle();      }    }  }</script><style scoped lang="less">  .demo_checkbox{    background: #fff;    margin-top: 5px;  }  .icon{    width: 24px;  }</style>