<template>  <demo-section>    <demo-block title="基本用法" class="demo_block">      <div style="text-align: center">        <x-circle                v-model="currentRate"                :rate="30"                :speed="100"                :text="text">        </x-circle>      </div>    </demo-block>    <demo-block title="样式定制" class="demo_block">      <div style="text-align: center">        <x-circle                v-model="currentRate"                color="#13ce66"                fill="#fff"                size="120px"                layer-color="#eee"                :text="text"                :rate="100"                :speed="100"                :clockwise="false"                :stroke-width="60">        </x-circle>      </div>    </demo-block>  </demo-section></template><script>  import demoSection from '../../common/demoSection'  import demoBlock from '../../common/demoBlock'  export default {    name: "index",    components:{      demoSection,      demoBlock    },    data() {      return {        currentRate: 0      };    },    computed:{      text() {        return this.currentRate.toFixed(0) + '%'      }    },    methods:{    }  }</script><style scoped lang="less">  .demo_block{    .x-circle{      margin: 0 auto;    }  }</style>