<template>  <demo-section>    <demo-block title="基本用法" class="demo_block">      <x-row>        <x-col span="8">span: 8</x-col>        <x-col span="8">span: 8</x-col>        <x-col span="8">span: 8</x-col>      </x-row>      <x-row>        <x-col span="4">span: 4</x-col>        <x-col span="10" offset="4">offset: 4, span: 10</x-col>      </x-row>      <x-row>        <x-col offset="12" span="12">offset: 12, span: 12</x-col>      </x-row>    </demo-block>    <demo-block title="flex布局" class="demo_block">      <!-- 左对齐 -->      <x-row type="flex">        <x-col span="6">span: 6</x-col>        <x-col span="6">span: 6</x-col>        <x-col span="6">span: 6</x-col>      </x-row>      <!-- 居中 -->      <x-row type="flex" justify="center">        <x-col span="6">span: 6</x-col>        <x-col span="6">span: 6</x-col>        <x-col span="6">span: 6</x-col>      </x-row>      <!-- 右对齐 -->      <x-row type="flex" justify="end">        <x-col span="6">span: 6</x-col>        <x-col span="6">span: 6</x-col>        <x-col span="6">span: 6</x-col>      </x-row>      <!-- 两端对齐 -->      <x-row type="flex" justify="space-between">        <x-col span="6">span: 6</x-col>        <x-col span="6">span: 6</x-col>        <x-col span="6">span: 6</x-col>      </x-row>      <!-- 每个元素的两侧间隔相等 -->      <x-row type="flex" justify="space-around">        <x-col span="6">span: 6</x-col>        <x-col span="6">span: 6</x-col>        <x-col span="6">span: 6</x-col>      </x-row>    </demo-block>  </demo-section></template><script>  import demoSection from '../../common/demoSection'  import demoBlock from '../../common/demoBlock'  export default {    name: "index",    components:{      demoSection,      demoBlock    }  }</script><style scoped lang="less">  .demo_block{    .x-col{      text-align: center;      line-height: 40px;      background: #efbf14;      &:nth-child(2n){        background: #e52a3c;      }    }    .x-row{      margin-bottom: 5px;    }  }</style>