<template>  <div>    <x-nav-bar            :title="title"            left-arrow            @click-left="onClickLeft"            @click-right="onClickRight">      <x-icon name="likeempty" slot="right" class="ricon"/>    </x-nav-bar>    <div class="demoContent">      <slot></slot>    </div>  </div></template><script>  export default {    name: "demoSection",    props:{      title:{        type:String      }    },    methods:{      onClickLeft(){        this.$router.push('/demo')        let {hash} = window.parent.location        if(hash.indexOf('#/demo')<0){          window.parent.location.href=`#/docs`        }      },      onClickRight(){        this.$toast('呵呵')      }    }  }</script><style scoped lang="less">  .demoContent{    padding: 10px;  }  .ricon{    font-size: 20px;  }</style>