<template>  <demo-section>    <demo-block title="完整时间">      <x-datetime-picker              v-model="currentDate"              type="datetime"              :min-date="minDate"              :max-date="maxDate"      />    </demo-block>    <demo-block title="年月日">      <x-datetime-picker              v-model="currentDate"              type="date"              :min-date="minDate"      />    </demo-block>    <demo-block title="年月">      <x-datetime-picker              v-model="currentDate"              type="year-month"              :min-date="minDate"              :formatter="formatter"      />    </demo-block>    <demo-block title="时间">      <x-datetime-picker              v-model="currentTime"              type="time"              :min-hour="0"              :max-hour="24"      />    </demo-block>  </demo-section></template><script>  import demoSection from '../../common/demoSection'  import demoBlock from '../../common/demoBlock'  export default {    name: "index",    components: {      demoSection,      demoBlock    },    data() {      return {        minHour: 10,        maxHour: 20,        minDate: new Date(),        maxDate: new Date(2019, 10, 1),        currentDate: new Date(),        currentTime:'12:30'      };    },    methods: {      formatter(type, value) {        if (type === 'year') {          return `${value}年`;        } else if (type === 'month') {          return `${value}月`        }        return value;      }    }  }</script><style scoped lang="less"></style>