<template>  <demo-section>    <demo-block title="alert 提示">      <x-button type="warning" @click="showAlert"> 弹出alert</x-button>    </demo-block>    <demo-block title="confirm 提示">      <x-button type="warning" @click="showConfirm"> 弹出Confirm</x-button>    </demo-block>  </demo-section></template><script>  import demoSection from '../../common/demoSection'  import demoBlock from '../../common/demoBlock'  export default {    name: "index",    components: {      demoSection,      demoBlock    },    methods:{      showAlert(){        this.$dialog.alert({          title:"标题",          message: '弹窗内容'        });      },      showConfirm(){        this.$dialog.confirm({          title: '标题',          message: '弹窗内容'        }).then(() => {          // on confirm        }).catch(() => {          // on cancel        });      }    }  }</script><style scoped lang="less"></style>