<template>  <demo-section>    <demo-block title="基础用法">      <x-cell-group>        <x-field v-model="value" placeholder="请输入用户名" />      </x-cell-group>    </demo-block>    <demo-block title="自定义类型">      <x-cell-group>        <x-field                v-model="username"                required                clearable                label="用户名"                icon="question"                placeholder="请输入用户名"                @click-icon="$toast('question')"        />        <x-field                v-model="password"                type="password"                label="密码"                placeholder="请输入密码"                required        />      </x-cell-group>    </demo-block>    <demo-block title="禁用输入框">      <x-cell-group>        <x-field                value="输入框已禁用"                label="用户名"                left-icon="contact"                disabled        />      </x-cell-group>    </demo-block>    <demo-block title="错误提示">      <x-cell-group>        <x-field                v-model="username"                label="用户名"                placeholder="请输入用户名"                error        />        <x-field                v-model="phone"                label="手机号"                placeholder="请输入手机号"                error-message="手机号格式错误"        />      </x-cell-group>    </demo-block>    <demo-block title="自适应高度">      <x-cell-group>        <x-field                v-model="message"                label="留言"                type="textarea"                placeholder="请输入留言"                rows="1"                autosize        />      </x-cell-group>    </demo-block>    <demo-block title="验证吗">      <x-cell-group>        <x-field                v-model="sms"                center                clearable                label="短信验证码"                placeholder="请输入短信验证码"        >          <x-button slot="button" size="small" type="warning">发送验证码</x-button>        </x-field>      </x-cell-group>    </demo-block>  </demo-section></template><script>  import demoSection from '../../common/demoSection'  import demoBlock from '../../common/demoBlock'  export default {    name: "index",    components: {      demoSection,      demoBlock    },    data(){      return {        value:'healen',        username:'',        password:'',        phone:'',        message:'',        sms:''      }    }  }</script><style scoped lang="less"></style>