<template>  <demo-section>    <demo-block title="基础用法">      <x-list v-model="loading" :finished="finished" @load="onLoad">        <x-cell v-for="item in list" :key="item" :title="item"></x-cell>      </x-list>    </demo-block>  </demo-section></template><script>  import demoSection from '../../common/demoSection'  import demoBlock from '../../common/demoBlock'  export default {    name: "index",    components: {      demoSection,      demoBlock    },    data() {      return {        list: [],        loading: false,        finished: false      };    },    methods: {      onLoad() {        // 异步更新数据        setTimeout(() => {          for (let i = 0; i < 10; i++) {            this.list.push(this.list.length + 1);          }          // 加载状态结束          this.loading = false;          // 数据全部加载完成          if (this.list.length >= 40) {            this.finished = true;          }        }, 500);      }    }  }</script><style scoped lang="less"></style>