<template>  <demo-section>    <demo-block title="基础用法 扫描手机浏览">      <x-button @click="show = !show">        {{show?'关闭':'弹出'}}默认键盘      </x-button>      <x-number-keyboard              :show="show"              extra-key="."              close-button-text="完成"              @blur="show = false"              @input="onInput"              @delete="onDelete"      />    </demo-block>    <demo-block title="弹出自定义键盘 扫描手机浏览">      <x-button @click="show1 = !show1">        {{show1?'关闭':'弹出'}}自定义键盘      </x-button>      <x-number-keyboard              :show="show1"              theme="custom"              extra-key="."              close-button-text="完成"              @blur="show1 = false"              @input="onInput"              @delete="onDelete"      />    </demo-block>    <demo-block title="扫一扫">      <img :src="require('./img/qrcode.png')"  class="erweim">    </demo-block>  </demo-section></template><script>  import demoSection from '../../common/demoSection'  import demoBlock from '../../common/demoBlock'  export default {    name: "index",    components: {      demoSection,      demoBlock    },    data() {      return {        show: false,        show1: false      }    },    methods: {      onInput(value) {        this.$toast(value);      },      onDelete() {        this.$toast('delete');      }    }  }</script><style scoped lang="less">  .erweim{    display: block;    margin: 0 auto;    width: 100px;  }</style>