<template>  <demo-section>    <demo-block title="扫一扫">      <img :src="require('./img/qrcode.png')" alt="" width="80px" class="erweim">      <x-button type="primary" @click="showKeyboard=!showKeyboard">        输入密码      </x-button>    </demo-block>      <!-- 密码输入框 -->      <x-password-input              :value="value"              info="密码为 6 位数字"              v-if="showKeyboard"              @focus="showKeyboard = false"      />      <!-- 数字键盘 -->      <x-number-keyboard              :show="showKeyboard"              @input="onInput"              @delete="onDelete"              @blur="showKeyboard = false"      />  </demo-section></template><script>  import demoSection from '../../common/demoSection'  import demoBlock from '../../common/demoBlock'  export default {    name: "index",    components: {      demoSection,      demoBlock    },    data() {      return {        value: '123',        showKeyboard: false      };    },    methods: {      onInput(key) {        this.value = (this.value + key).slice(0, 6);      },      onDelete() {        this.value = this.value.slice(0, this.value.length - 1);      }    }  }</script><style scoped lang="less">  .erweim{    display: block;    margin: 0 auto;    width: 100px;  }</style>