<template>  <demo-section>    <demo-block title="基础用法">      <x-picker :columns="columns" @change="onChange" />    </demo-block>    <demo-block title="禁用选项">      <x-picker :columns="columns1"/>    </demo-block>    <demo-block title="展示顶部条">      <x-picker              show-toolbar              title="标题"              :columns="columns"      />    </demo-block>    <demo-block title="多级联动">      <x-picker :columns="columns3" @change="onChange1" />    </demo-block>  </demo-section></template><script>  import demoSection from '../../common/demoSection'  import demoBlock from '../../common/demoBlock'  const citys = {    '战士': ['典韦', '亚瑟', '赵云', '关羽', '凯'],    '法师': ['小乔', '张良', '王昭君','安琪拉','扁鹊']  };  export default {    name: "index",    components: {      demoSection,      demoBlock    },    data() {      return {        columns: ['猴子', '伽罗', '后羿', '阿轲', '貂蝉'],        columns1: [          { text: '凯', disabled: true },          { text: '马可波罗' },          { text: '猴子' }        ],        columns3: [          {            values: Object.keys(citys),            className: 'column1'          },          {            values: citys['浙江'],            className: 'column2',            defaultIndex: 2          }        ]      };    },    methods: {      onChange(picker, value, index) {        Toast(`当前值：${value}, 当前索引：${index}`);      },      onChange1(picker, values) {        picker.setColumnValues(1, citys[values[0]]);      }    }  }</script><style scoped lang="less"></style>