<template>  <demo-section>    <demo-block title="基础用法">        <x-pull-refresh v-model="isLoading" @refresh="onRefresh" class="refre">          <p>刷新{{ count }} 次</p>        </x-pull-refresh>    </demo-block>  </demo-section></template><script>  import demoSection from '../../common/demoSection'  import demoBlock from '../../common/demoBlock'  export default {    name: "index",    components: {      demoSection,      demoBlock    },    data() {      return {        count: 0,        isLoading: false      }    },    methods: {      onRefresh() {        setTimeout(() => {          this.$toast('刷新成功');          this.isLoading = false;          this.count++;        }, 500);      }    }  }</script><style scoped lang="less">.refre{  background: #fff;  height: 200px;}</style>