<template>  <demo-section>    <demo-block title="基础用法">      <x-radio-group v-model="radio">        <x-radio name="1">单选框 1</x-radio>        <x-radio name="2">单选框 2</x-radio>      </x-radio-group>    </demo-block>    <demo-block title="禁用" disabled>      <x-radio-group v-model="radio1" disabled>        <x-radio name="1">单选框 1</x-radio>        <x-radio name="2">单选框 2</x-radio>      </x-radio-group>    </demo-block>    <demo-block title="与 Cell 组件一起使用">      <x-radio-group v-model="radio3">        <x-cell-group>          <x-cell title="单选框 1" clickable @click="radio3 = '1'">            <x-radio name="1" />          </x-cell>          <x-cell title="单选框 2" clickable @click="radio3 = '2'">            <x-radio name="2" />          </x-cell>        </x-cell-group>      </x-radio-group>    </demo-block>  </demo-section></template><script>  import demoSection from '../../common/demoSection'  import demoBlock from '../../common/demoBlock'  export default {    name: "index",    components: {      demoSection,      demoBlock    },    data(){      return {        radio:1,        radio1:1,        radio3:1      }    }  }</script><style scoped lang="less">  .x-radio{    height: 30px;  }</style>