<template>  <demo-section style="background: #fff">    <demo-block title="基础用法">      <div class="serbox">        <x-search placeholder="请输入搜索关键词" v-model="value" />      </div>    </demo-block>    <demo-block title="监听对应事件">      <div class="serbox">        <form action="/">          <x-search                  v-model="value3"                  placeholder="请输入搜索关键词"                  show-action                  @search="onSearch"                  @cancel="onCancel"          />        </form>      </div>    </demo-block>    <demo-block title="自定义按钮">      <div class="serbox">        <x-search                v-model="value3"                placeholder="请输入搜索关键词"                show-action                @search="onSearch"        >          <div slot="action" @click="onSearch">搜索</div>        </x-search>      </div>    </demo-block>  </demo-section></template><script>  import demoSection from '../../common/demoSection'  import demoBlock from '../../common/demoBlock'  export default {    name: "index",    components: {      demoSection,      demoBlock    },    data(){      return {        value:"",        value2:"",        value3:""      }    },    methods:{      onSearch(){        this.$toast('search')      },      onCancel(){        this.$toast('取消')      }    }  }</script><style scoped lang="less">  body,html{    background: #ffffff;  }  .serbox{    background: #fff;  }</style>