<template>  <demo-section>    <demo-block title="基本用法">      <div style="padding: 10px">        <x-slider v-model="value1" @change="onChange" />      </div>    </demo-block>    <demo-block title="指定选择范围">      <div style="padding: 10px">        <x-slider v-model="value2" :min="10" :max="90" />      </div>    </demo-block>    <demo-block title="禁用">      <div style="padding: 10px">        <x-slider v-model="value3" disabled />      </div>    </demo-block>    <demo-block title="指定步长">      <div style="padding: 10px">        <x-slider v-model="value4" :step="10" bar-height="4px" />      </div>    </demo-block>  </demo-section></template><script>  import demoSection from '../../common/demoSection'  import demoBlock from '../../common/demoBlock'  export default {    name: "index",    components: {      demoSection,      demoBlock    },    data() {      return {        value1: 50,        value2:20,        value3:10,        value4:10      };    },    methods: {      onChange(value) {        this.$toast('当前值：' + value);      }    }  }</script><style scoped lang="less"></style>