<template>  <demo-section >    <demo-block title="基础用法">      <x-swipe :autoplay="3000" class="demo-swipe">        <x-swipe-item>1</x-swipe-item>        <x-swipe-item>2</x-swipe-item>        <x-swipe-item>3</x-swipe-item>        <x-swipe-item>4</x-swipe-item>      </x-swipe>    </demo-block>    <demo-block title="监听Change事件">      <x-swipe @change="onChange" class="demo-swipe">        <x-swipe-item>1</x-swipe-item>        <x-swipe-item>2</x-swipe-item>        <x-swipe-item>3</x-swipe-item>        <x-swipe-item>4</x-swipe-item>      </x-swipe>    </demo-block>    <demo-block title="控制大小">      <x-swipe :autoplay="3000" :width="300" class="demo-swipe">        <x-swipe-item>1</x-swipe-item>        <x-swipe-item>2</x-swipe-item>        <x-swipe-item>3</x-swipe-item>        <x-swipe-item>4</x-swipe-item>      </x-swipe>    </demo-block>    <demo-block title="lazy-load" class="demo-swipe">      <x-swipe :autoplay="3000" >        <x-swipe-item v-for="img in imageList">          <img class="pice" v-lazy="img">        </x-swipe-item>      </x-swipe>    </demo-block>    <demo-block title="自定义指示器">      <x-swipe @change="onChange1" class="demo-swipe">        <x-swipe-item>1</x-swipe-item>        <x-swipe-item>2</x-swipe-item>        <x-swipe-item>3</x-swipe-item>        <x-swipe-item>4</x-swipe-item>        <div class="custom-indicator" slot="indicator">          {{ current + 1 }}/4        </div>      </x-swipe>    </demo-block>  </demo-section></template><script>  import demoSection from '../../common/demoSection'  import demoBlock from '../../common/demoBlock'  import Vue from 'vue'  import {Lazyload} from 'x-ui'  Vue.use(Lazyload)  export default {    name: "index",    components: {      demoSection,      demoBlock    },    data() {      return {        imageList: [          'https://raw.githubusercontent.com/healen/xui/gh-pages/a/1.jpeg',          'https://raw.githubusercontent.com/healen/xui/gh-pages/a/2.jpeg',          'https://raw.githubusercontent.com/healen/xui/gh-pages/a/3.jpeg',          'https://raw.githubusercontent.com/healen/xui/gh-pages/a/4.jpeg',          'https://raw.githubusercontent.com/healen/xui/gh-pages/a/5.jpeg',        ],        current: 0      };    },    methods: {      onChange(index) {        this.$toast('当前 Swipe 索引：' + index);      },      onChange1(index) {        this.current = index;      }    }  }</script><style scoped lang="less">  .demo-swipe {    padding-bottom: 30px;    .x-swipe {      cursor: pointer;      &-item {        color: #fff;        font-size: 20px;        text-align: center;        line-height: 150px;        &:nth-child(even) {          background-color: #39a9ed;        }        &:nth-child(odd) {          background-color: #66c6f2;        }      }      img {        width: 100%;        display: block;        box-sizing: border-box;        background-color: #fff;        pointer-events: none;      }    }    &--vertical {      height: 200px;      .van-swipe-item {        line-height: 200px;      }    }    .custom-indicator {      position: absolute;      right: 5px;      bottom: 5px;      padding: 2px 5px;      font-size: 12px;      color: #fff;      background: rgba(0, 0, 0, .1);    }  }</style>