<template>  <demo-section>    <demo-block title="基础用法">      <x-tabs v-model="active"  class="demo-tab">        <x-tab title="标签 1">内容 1</x-tab>        <x-tab title="标签 2">内容 2</x-tab>        <x-tab title="标签 3">内容 3</x-tab>        <x-tab title="标签 4">内容 4</x-tab>      </x-tabs>    </demo-block>    <demo-block title="大于4个横向滚动">      <x-tabs  class="demo-tab">        <x-tab v-for="index in 8" :title="'标签 ' + index">          内容 {{ index }}        </x-tab>      </x-tabs>    </demo-block>    <demo-block title="粘性布局">      <x-tabs sticky class="demo-tab">        <x-tab v-for="index in 4" :title="'选项 ' + index">          <p>内容 {{ index }}</p>          <p>内容 {{ index }}</p>          <p>内容 {{ index }}</p>          <p>内容 {{ index }}</p>          <p>内容 {{ index }}</p>          <p>内容 {{ index }}</p>          <p>内容 {{ index }}</p>          <p>内容 {{ index }}</p>          <p>内容 {{ index }}</p>          <p>内容 {{ index }}</p>          <p>内容 {{ index }}</p>          <p>内容 {{ index }}</p>          <p>内容 {{ index }}</p>          <p>内容 {{ index }}</p>          <p>内容 {{ index }}</p>          <p>内容 {{ index }}</p>        </x-tab>      </x-tabs>    </demo-block>    <demo-block title="禁用标签">      <x-tabs @disabled="onClickDisabled" class="demo-tab">        <x-tab title="标签 1">内容 1</x-tab>        <x-tab title="标签 2" disabled>内容 2</x-tab>        <x-tab title="标签 3">内容 3</x-tab>      </x-tabs>    </demo-block>    <demo-block title="风格">      <x-tabs type="card" class="demo-tab1">        <x-tab title="标签 1">内容 1</x-tab>        <x-tab title="标签 3">内容 3</x-tab>      </x-tabs>    </demo-block>    <demo-block title="点击">      <x-tabs @click="onClick" class="demo-tab">        <x-tab title="标签 1">内容 1</x-tab>        <x-tab title="标签 2">内容 2</x-tab>        <x-tab title="标签 3">内容 3</x-tab>      </x-tabs>    </demo-block>    <demo-block title="滑动切换">      <x-tabs swipeable class="demo-tab">        <x-tab v-for="index in 4" :title="'选项 ' + index">          内容 {{ index }}        </x-tab>      </x-tabs>    </demo-block>  </demo-section></template><script>  import demoSection from '../../common/demoSection'  import demoBlock from '../../common/demoBlock'  export default {    name: "index",    components: {      demoSection,      demoBlock    },    data() {      return {        active: 2      };    },    methods:{      onClickDisabled(index,title){        this.$toast(title + '已被禁用');      },      onClick(index, title) {        this.$toast(title);      }    }  }</script><style scoped lang="less">  .demo-tab1{    .x-tab__pane {      padding: 10px 20px;    }  }  .demo-tab {    .x-tab .x-icon {      margin-right: 5px;      vertical-align: -2px;    }    .x-tab__pane {      background-color: #fff;      padding: 20px;      border-top: 1px solid #efefef;    }    .x-tabs--card .x-tab__pane {      background-color: transparent;      .x-tab__pane{        background-color: #ff0000!important;      }    }    .custom-tabwrap .x-tab-active {      color: #20a0ff;    }    .custom-tabwrap .x-tabs-nav-bar {      background: #20a0ff;    }    .custom-pane {      text-align: center;      height: 50px;      line-height: 50px;    }    .x-doc-demo-block:last-child {      .x-tab__pane {        padding: 50px 20px;      }    }  }</style>