<template>  <demo-section>    <demo-block>      <div class="buttondemo">        <x-button type="primary" @click="tost(1)" >文字提示</x-button>        <x-button type="warning" @click="tost(2)">加载提示</x-button>        <x-button type="danger" @click="tost(3)">成功</x-button>        <x-button type="danger" @click="tost(4)">失败</x-button>        <x-button type="primary" @click="tost(5)">高级提示</x-button>      </div>    </demo-block>  </demo-section></template><script>  import demoSection from '../../common/demoSection'  import demoBlock from '../../common/demoBlock'  export default {    name: "index",    components: {      demoSection,      demoBlock    },    methods:{      tost(num){        switch (num){          case 1:            this.$toast('我是文字提示！');            break          case 2:            this.$toast.loading({              mask: true,              message: '加载中...'            });            break          case 3:            this.$toast.success('成功文案');            break          case 4:            this.$toast.fail('失败文案');            break          default:            let toast = this.$toast.loading({              duration: 0,       // 持续展示 toast              forbidClick: true, // 禁用背景点击              loadingType: 'spinner',              message: '倒计时 3 秒'            });            let second = 3;            const timer = setInterval(() => {              second--;              if (second) {                toast.message = `倒计时 ${second} 秒`;              } else {                clearInterval(timer);                this.$toast.clear();              }            }, 1000);            break        }      }    }  }</script><style scoped lang="less">  .buttondemo{    .x-button{      margin-right: 10px;      margin-bottom: 10px;    }  }</style>